function holdFocusOnTab(element=null,hold=!0,tabString="\t"){const _ignoreTabKey=(e)=>{if(e.key==='Tab'){e.preventDefault();if(element.tagName==="TEXTAREA"){let start=this.selectionStart;let end=this.selectionEnd;this.value=this.value.substring(0,start)+"\t"+this.value.substring(end);this.selectionStart=this.selectionEnd=start+1}else if(element.isContentEditable){let selection=document.getSelection();if(selection!=='null'&&selection.type!=="None"){let range=selection.getRangeAt(0);let start=range.startOffset;let end=range.endOffset;let value=this.innerHTML;this.innerHTML=value.substring(0,start)+tabString+value.substring(end,value.length);selection.removeAllRanges();range=document.createRange();let container=this.childNodes[0];range.setStart(container,start+tabString.length);range.setEnd(container,start+tabString.length);selection.addRange(range)}}}};if(element===null){let elements=document.getElementsByTagName("*");for(let i=0;i<elements.length;i++){if(elements[i].tagName==="TEXTAREA"||elements[i].isContentEditable){holdFocusOnTab(elements[i],hold,tabString)}}}else{element.removeEventListener('keydown',_ignoreTabKey);if(hold)element.addEventListener('keydown',_ignoreTabKey);}}
